import Link from "next/link";

class Listlink extends React.Component {
  constructor(props) {
    super(props);
    this.ChangeHandle = this.ChangeHandle.bind(this);
    this.state = {
      list: [
        "Getting Started",
        "Instruction",
        "Download",
        "Contents",
        "Browsers & Devices",
        "Theming"
      ],
      IndexState: 0
    };
  }

  ChangeHandle = e => {
    console.log(e);
    this.setState({ IndexState: e });
  };

  render() {
    return (
      <div className="sideBar border">
        <ul>
          {this.state.list.map((li, i) => (
            <li key={i}>
              <Link href="/education">
                <a value={i} onClick={this.ChangeHandle.bind(this, i)}>
                  {li}
                </a>
              </Link>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default Listlink;
