import Link from "next/link";
import Head_Banner from "../components/Head_Banner";
import Rowpic from "../components/picRow";
import balancedData from "../db/balanced.json";
import Layout from "../layouts/Layout";
import "../styles/styles.scss";

class Balanced extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  iterates() {
    const data = balancedData.data;
    data.map(item => {
      <Rowpic item={item} />;
    });
  }

  render() {
    const data = balancedData.data;
    return (
      <Layout>
        <Head_Banner
          text_1={"BALANCED INVESTMENT"}
          text_2={"Less risk, but more profit."}
        />
        <div className="cardContainer text-center">
          {data.map(item => {
            <li>
              <div className="cardContents" key={item.id}>
                <div className="card">
                  <img
                    class="card-img-top"
                    src={item.src}
                    alt="Card image cap"
                  />
                  <div className="card-body">
                    <p className="card-text">
                      <Link href="#">
                        <a>{item.title}</a>
                      </Link>
                    </p>
                  </div>
                </div>
              </div>
            </li>;
          })}
        </div>
      </Layout>
    );
  }
}

export default Balanced;
